// Config starter code
import { createChatBotMessage } from "react-chatbot-kit";

const config = {
  botName: "learningBot",
  initialMessages: [createChatBotMessage(`Hello, welcome to my portfolio`)],
  customStyles: {
    botMessageBox: {
      backgroundColor: '#376B7E',
    },
    chatButton: {
      backgroundColor: '#5ccc9d',
    },
  },

}

export default config
